import {
    type GQLCreateSampleMutation,
    type GQLCreateSampleMutationVariables,
    type GQLDeleteSampleMutation,
    type GQLDeleteSampleMutationVariables,
    type GQLSamplesFormFragment,
    type GQLSamplesGridQuery,
    type GQLSamplesGridQueryVariables,
} from "./Sample.generated";

const samplesFragment = gql`
    fragment SamplesForm on Sample {
        id
        sample
    }
`;

const samplesQuery = gql`
    query SamplesGrid($offset: Int!, $limit: Int!, $sort: [SampleSort!], $search: String, $filter: SampleFilter) {
        samples(offset: $offset, limit: $limit, sort: $sort, search: $search, filter: $filter) {
            nodes {
                ...SamplesForm
            }
            totalCount
        }
    }
    ${samplesFragment}
`;

const deleteSampleMutation = gql`
    mutation DeleteSample($id: ID!) {
        deleteSample(id: $id)
    }
`;

const createSampleMutation = gql`
    mutation CreateSample($input: SampleInput!) {
        createSample(input: $input) {
            id
        }
    }
`;

function SamplesGridToolbar() {
    return (
        <DataGridToolbar>
            <ToolbarItem>
                <GridToolbarQuickFilter />
            </ToolbarItem>
            <ToolbarItem>
                <GridFilterButton />
            </ToolbarItem>
            <FillSpace />
            <ToolbarActions>
                <Button responsive startIcon={<AddIcon />} component={StackLink} pageName="add" payload="add">
                    <FormattedMessage id="sample.samplesForm.newEntry" defaultMessage={`New Sample`} />
                </Button>
            </ToolbarActions>
        </DataGridToolbar>
    );
}

export function SamplesGrid() {
    const client = useApolloClient();
    const intl = useIntl();
    const dataGridProps = { ...useDataGridRemote(), ...usePersistentColumnState("SamplesGrid") };
    const columns: GridColDef<GQLSamplesFormFragment>[] = [
        { field: "sample", headerName: intl.formatMessage({ id: "sample.sample", defaultMessage: "Sample" }), flex: 1, minWidth: 150 },
        {
            field: "actions",
            headerName: "",
            sortable: false,
            filterable: false,
            type: "actions",
            align: "right",
            pinned: "right",
            width: 84,
            renderCell: (params) => {
                return (
                    <>
                        <IconButton color="primary" component={StackLink} pageName="edit" payload={params.row.id}>
                            <EditIcon />
                        </IconButton>
                        <CrudContextMenu
                            copyData={() => {
                                // Don't copy id, because we want to create a new entity with this data
                                const { id, ...filteredData } = filterByFragment(samplesFragment, params.row);
                                return filteredData;
                            }}
                            onPaste={async ({ input }) => {
                                await client.mutate<GQLCreateSampleMutation, GQLCreateSampleMutationVariables>({
                                    mutation: createSampleMutation,
                                    variables: { input },
                                });
                            }}
                            onDelete={async () => {
                                await client.mutate<GQLDeleteSampleMutation, GQLDeleteSampleMutationVariables>({
                                    mutation: deleteSampleMutation,
                                    variables: { id: params.row.id },
                                });
                            }}
                            refetchQueries={[samplesQuery]}
                        />
                    </>
                );
            },
        },
    ];

    const { filter: gqlFilter, search: gqlSearch } = muiGridFilterToGql(columns, dataGridProps.filterModel);

    const { data, loading, error } = useQuery<GQLSamplesGridQuery, GQLSamplesGridQueryVariables>(samplesQuery, {
        variables: {
            filter: gqlFilter,
            search: gqlSearch,
            offset: dataGridProps.paginationModel.page * dataGridProps.paginationModel.pageSize,
            limit: dataGridProps.paginationModel.pageSize,
            sort: muiGridSortToGql(dataGridProps.sortModel),
        },
    });
    const rowCount = useBufferedRowCount(data?.samples.totalCount);
    if (error) throw error;
    const rows = data?.samples.nodes ?? [];

    return (
        <DataGridPro
            {...dataGridProps}
            rows={rows}
            rowCount={rowCount}
            columns={columns}
            loading={loading}
            slots={{
                toolbar: SamplesGridToolbar as GridSlotsComponent["toolbar"],
            }}
        />
    );
}
